import React from 'react';


class Signin extends React.Component {

    constructor(props){
        super(props);  //because we need to access the props
        this.state = {
            signInEmail : '',
            signInPassword : ''
        }
    }

    onEmailChange = (event) => {
        this.setState({signInEmail: event.target.value})
    }


    onPsswordChange = (event) => {
        this.setState({signInPassword: event.target.value})
    }

    onSubmitSignIn = () => {
        fetch('http://localhost:3000/signin',{     //fetch by default acceps GET thats why we are defining a second parameter
            method: 'post',  
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify({
                email: this.state.signInEmail,
                password: this.state.signInPassword
            })
        })
            .then(response => response.json()) //TO verify signing in //
            .then(data => {
                if(data === 'success'){
                    this.props.onRouteChange('home')
                }
            })

    }

    render() {
        return(
            <div>
                <article className="center br3 ba  b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5">
                    <main className="pa4 black-80">
                    <div className="measure">
                    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                        <legend className="f1 fw6 ph0 mh0">Sign In</legend>
                        <div className="mt3">
                        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                        <input
                        onChange={this.onEmailChange} 
                        className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                        type="email" 
                        name="email-address"  
                        id="email-address"/>
                        </div>
                        <div className="mv3">
                        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                        <input
                        onChange={this.onPsswordChange} 
                        className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                        type="password" 
                        name="password"  
                        id="password"/>
                        </div>
                    </fieldset>
                    <div className="">
                        <input 
                        onClick= {this.onSubmitSignIn }
                        className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                        type="submit"
                         value="Sign in"/>
                    </div>
                    <div className="lh-copy mt3">
                        <p  onClick= {() => this.props.onRouteChange('register') } className="pointer f6 link dim black db">Register</p>

                    </div>
                    </div>
                </main>
            </article>
          </div>

        );

    }

}

export default Signin
